base.query('insert into questions(id, question, revision, objective_id) values(?, ?, ?, ?)', [89, "Devant un ictère, quels signes échographiques permettent de porter le diagnostic d’obstacle extra- hépatique ?", "Un obstacle défini une dilatation en amont à cause de la stase de la bile. <br />Un obstacle à l’écoulement sur les voies biliaires extra hépatique s’accompagnera d’une dilatation des voies biliaires intra hépatiques.", 10], function() {progress()})
base.query('insert into questions(id, question, revision, objective_id) values(?, ?, ?, ?)', [90, "Devant un ictère cholestatique sans grosse vésicule, mais avec dilatation des voies biliaires intra hépatiques et cholédoque normal à l’échographie, quel diagnostic faut-il évoquer ?", "L’hépatite cholestatique ne s’accompagne pas de dilatation des voies biliaires, et le cancer de la tête du pancréas s’accompagne d’une grosse vésicule caractériqtique.<br />Le cancer du hile du foie, quand à lui donne un aspect du cholédoque normal car l’obstacle est plus haut situé, pas de stase en aval du cystique, donc vésicule vide, et une dilatation isolée des voies biliaires intra hépatiques.<br />", 10], function() {progress()})
